const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_02_Security_Firewall';
const VERSION = 'v2.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'security', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const items = $input.all();
  if (!items?.length) return fail('VAL_NO_INPUT', 'No input received');

  const raw = items[0].json.body || items[0].json;

  if (!raw.telegram_id) return fail('VAL_MISSING_ID', 'telegram_id is required');

  const telegramId = String(raw.telegram_id);
  if (!/^\d+$/.test(telegramId)) {
    return fail('VAL_INVALID_ID', 'telegram_id must be a valid numeric ID');
  }

  return ok({
    telegram_id: telegramId,
    action: raw.action || 'access',
    ip: raw.ip || null
  });
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_02_Security_Firewall';
const VERSION = 'v2.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'security', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const items = $input.all();
  const telegramId = $('Guard').item.json.data.telegram_id;

  // If no record exists, user is allowed
  if (!items?.length || (items.length === 1 && Object.keys(items[0].json).length === 0)) {
    return ok({ telegram_id: telegramId, access: 'granted', reason: 'new_user' });
  }

  const record = items[0].json;
  const now = DateTime.now().setZone(TIMEZONE);

  // Check if blocked
  if (record.is_blocked) {
    const blockedUntil = record.blocked_until ? DateTime.fromISO(record.blocked_until) : null;

    // If still blocked
    if (!blockedUntil || blockedUntil > now) {
      return fail('SEC_BLOCKED', `User is blocked until ${blockedUntil?.toISO() || 'indefinite'}`);
    }
  }

  // Check strike count (rate limiting)
  const lastStrike = record.last_strike_at ? DateTime.fromISO(record.last_strike_at) : null;
  const hourAgo = now.minus({ hours: 1 });

  // If more than 10 strikes in the last hour, temporary block
  if (record.strike_count >= 10 && lastStrike && lastStrike > hourAgo) {
    return fail('SEC_RATE_LIMITED', 'Too many requests. Please wait and try again.');
  }

  return ok({ 
    telegram_id: telegramId, 
    access: 'granted', 
    strike_count: record.strike_count,
    reason: 'verified'
  });
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_02_Security_Firewall';
const VERSION = 'v2.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'security', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const telegramId = $('Guard').item.json.data.telegram_id;
  const result = $input.first()?.json;

  return ok({
    telegram_id: telegramId,
    access: 'granted',
    strike_count: result?.strike_count || 1,
    timestamp: DateTime.now().setZone(TIMEZONE).toISO()
  });
} catch (e) {
  return [{ json: { success: true, error_code: null, error_message: null, data: { access: 'granted', warning: e.message }, _meta: meta() } }];
}

