const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_02_Security_Firewall';
const VERSION = 'v2.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'security', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });

try {
  const input = $input.all();
  if (!input?.length) {
    return [{ json: { success: true, error_code: null, error_message: null, data: { access: 'granted' }, _meta: meta() } }];
  }
  return input;
} catch (e) {
  return [{ json: { success: false, error_code: 'INTERNAL_ERROR', error_message: `${WORKFLOW_ID}: ${e.message}`, data: null, _meta: meta() } }];
}
