const WORKFLOW_ID = 'BB_03_02_ProviderData';
const meta = () => ({ source: 'guard', timestamp: new Date().toISOString(), workflow_id: WORKFLOW_ID });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const items = $input.all();
  if (!items?.length) return fail('VAL_NO_INPUT', 'No input received');

  const raw = items[0].json.body || items[0].json;

  if (!raw.provider_slug) {
    return fail('VAL_MISSING_SLUG', 'Missing provider_slug in input');
  }

  return ok({
    provider_slug: String(raw.provider_slug).toLowerCase().trim()
  });
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

const WORKFLOW_ID = 'BB_03_02_ProviderData';
const meta = () => ({ source: 'validate_db', timestamp: new Date().toISOString(), workflow_id: WORKFLOW_ID });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const items = $input.all();
  const result = items[0].json;

  // Check for DB error (onError=continue case)
  if (result.error) {
    return fail('DB_ERROR', `Database failed: ${result.error.message || 'Unknown DB error'}`);
  }

  // Check if result is empty (alwaysOutputData=true case)
  if (!result.id) {
    return fail('PROV_NOT_FOUND', 'Provider not found or inactive');
  }

  return ok(result);
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

const WORKFLOW_ID = 'BB_03_02_ProviderData';
const meta = () => ({ source: 'format_output', timestamp: new Date().toISOString(), workflow_id: WORKFLOW_ID });

try {
  const raw = $input.first()?.json;
  // If success=false is already there, pass it through
  if (raw.success === false) return [ { json: raw } ];

  return [{ 
    json: {
      success: true,
      error_code: null,
      error_message: null,
      data: raw.data || raw,
      _meta: meta()
    }
  }];
} catch (e) {
  return [{ 
    json: {
      success: false,
      error_code: 'INTERNAL_ERROR',
      error_message: `${WORKFLOW_ID}: ${e.message}`,
      data: null,
      _meta: meta()
    }
  }];
}
