const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_03_04_BookingsData';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'guard', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const items = $input.all();
  if (!items?.length) return fail('VAL_NO_INPUT', 'No input received');

  const raw = items[0].json.body || items[0].json;

  // Accept both provider_id (UUID) and provider_slug (string)
  const providerId = raw.provider_id;
  const providerSlug = raw.provider_slug;

  if (!providerId && !providerSlug) {
    return fail('VAL_MISSING_ID', 'Missing provider_id or provider_slug in input');
  }

  // Validate slug format if provided (prevent path traversal)
  if (providerSlug && !/^[a-z0-9-]+$/i.test(providerSlug)) {
    return fail('VAL_INVALID_SLUG', 'provider_slug must contain only alphanumeric characters and hyphens');
  }

  return ok({
    provider_id: providerId,
    provider_slug: providerSlug,
    start_date: raw.start_date || DateTime.now().setZone(TIMEZONE).toISO().split('T')[0]
  });
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_03_04_BookingsData';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'validate_db', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const items = $input.all();
  const first = items[0].json;

  // Check for DB error
  if (first.error) {
    return fail('DB_ERROR', `Database failed: ${first.error.message}`);
  }

  // For READ operations, empty result is NOT an error, it's an empty list
  if (!first.id) {
    return ok([]);
  }

  // Return all items as a list in 'data'
  return ok(items.map(i => i.json));
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_03_04_BookingsData';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'format_output', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });

try {
  const raw = $input.first()?.json;
  return [{ 
    json: {
      success: raw.success !== false,
      error_code: raw.error_code || null,
      error_message: raw.error_message || null,
      data: raw.data || (raw.success === false ? null : raw),
      _meta: meta()
    }
  }];
} catch (e) {
  return [{ json: { success: false, error_code: 'INTERNAL_ERROR', error_message: e.message, data: null, _meta: meta() } }];
}
