const WORKFLOW_ID = 'BB_04_Booking_Create';
const meta = () => ({ source: 'webhook', timestamp: new Date().toISOString(), workflow_id: WORKFLOW_ID });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

const isValidUUID = (v) => /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(v);
const sanitize = (str) => typeof str === 'string' ? str.replace(/[<>]/g, '') : str;

try {
  const items = $input.all();
  if (!items?.length) return fail('VAL_NO_INPUT', 'No input received');

  const raw = items[0].json.body || items[0].json;
  const errors = [];

  if (!raw.provider_id || !isValidUUID(raw.provider_id)) errors.push('provider_id must be valid UUID');
  if (!raw.user_id || !isValidUUID(raw.user_id)) errors.push('user_id must be valid UUID');

  if (!raw.start_time || isNaN(Date.parse(raw.start_time))) {
    errors.push('start_time must be valid ISO date');
  } else {
    const start = new Date(raw.start_time);
    const now = new Date();
    const maxDate = new Date();
    maxDate.setDate(now.getDate() + 90);

    if (start < now) errors.push('start_time must be in the future');
    if (start > maxDate) errors.push('start_time too far in the future (max 90 days)');
  }

  if (!raw.end_time || isNaN(Date.parse(raw.end_time))) {
    errors.push('end_time must be valid ISO date');
  } else if (raw.start_time && new Date(raw.start_time) >= new Date(raw.end_time)) {
    errors.push('start_time must be before end_time');
  }

  if (errors.length > 0) return fail('VAL_INVALID_INPUT', errors.join('; '));

  return ok({ provider_id: raw.provider_id, user_id: raw.user_id, service_id: raw.service_id || null, start_time: raw.start_time, end_time: raw.end_time });
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

