try {
  const items = $input.all();
  if (!items || items.length === 0) return fail('DB_ERROR', `${DB_CONTEXT}: no response from database`);

  const res = items[0].json;

  if (!res.provider_exists) return fail('BOOK_INVALID_PROVIDER', 'Provider not found or inactive');
