const meta = () => ({ source: 'subworkflow', timestamp: new Date().toISOString(), workflow_id: WORKFLOW_ID });

try {
  const input = $input.all();
  if (!input || input.length === 0) {
    return [{ json: { success: false, error_code: 'SYS_NO_OUTPUT', error_message: `${WORKFLOW_ID}: format output received no input`, data: null, _meta: meta() } }];
  }

  const incoming = input[0].json;

  if ('success' in incoming && '_meta' in incoming) {
    return [{ json: { ...incoming, _meta: { ...incoming._meta, timestamp: new Date().toISOString(), formatted_by: WORKFLOW_ID } } }];
  }

  return [{ json: { success: true, error_code: null, error_message: null, data: incoming, _meta: meta() } }];

} catch (e) {
  return [{ json: { success: false, error_code: 'INTERNAL_ERROR', error_message: `${WORKFLOW_ID} format: ${e.message}`, data: null, _meta: meta() } }];
