const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_04_Booking_Transaction';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'orchestrator', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

const ACTION_MAP = {
  'booking': 'create',
  'book': 'create',
  'agendar': 'create',
  'reservar': 'create',
  'create': 'create',
  'cancel': 'cancel',
  'cancelar': 'cancel',
  'anular': 'cancel',
  'reschedule': 'reschedule',
  'reagendar': 'reschedule',
  'cambiar': 'reschedule'
};

try {
  const items = $input.all();
  if (!items?.length) return fail('VAL_NO_INPUT', 'No input received');

  const raw = items[0].json.body || items[0].json;
  const text = (raw.text || '').toLowerCase();

  let action = raw.action || 'create';

  if (!raw.action) {
    for (const [keyword, mappedAction] of Object.entries(ACTION_MAP)) {
      if (text.includes(keyword)) {
        action = mappedAction;
        break;
      }
    }
  }

  const validActions = ['create', 'cancel', 'reschedule'];
  if (!validActions.includes(action)) {
    action = 'create';
  }

  return ok({
    action: action,
    telegram_id: raw.telegram_id,
    chat_id: raw.chat_id,
    provider_id: raw.provider_id || null,
    user_id: raw.user_id || null,
    booking_id: raw.booking_id || null,
    start_time: raw.start_time || null,
    end_time: raw.end_time || null,
    service_id: raw.service_id || null,
    raw: raw
  });
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_04_Booking_Transaction';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'orchestrator', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const result = $input.first()?.json;
  const action = $('Determine Action').item.json.data.action;

  if (result.success) {
    return ok({
      action: action,
      result: result.data,
      message: `Booking ${action}d successfully`
    });
  } else {
    return [result];
  }
} catch (e) {
  return [{ json: { success: false, error_code: 'INTERNAL_ERROR', error_message: `${WORKFLOW_ID}: ${e.message}`, data: null, _meta: meta() } }];
}
