const WORKFLOW_ID = 'BB_04_Validate_Input';
const meta = () => ({ source: 'webhook', timestamp: new Date().toISOString(), workflow_id: WORKFLOW_ID });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

const validActions = ['booking', 'cancel', 'reschedule', 'booking_create', 'booking_cancel', 'booking_reschedule', 'list_bookings'];
const isValidUUID = (v) => /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(v);
const sanitize = (str) => typeof str === 'string' ? str.replace(/[<>]/g, '') : str;

try {
  const items = $input.all();
  if (!items?.length) return fail('VAL_NO_INPUT', 'No input received');

  let raw = items[0].json.body || items[0].json;
  const errors = [];

  if (!raw.action || !validActions.includes(raw.action)) errors.push(`Invalid action: ${sanitize(raw.action)}`);
  if (!raw.user_id || !isValidUUID(raw.user_id)) errors.push('user_id must be valid UUID');
  if (raw.provider_id && !isValidUUID(raw.provider_id)) errors.push('provider_id must be valid UUID');

  if (errors.length > 0) return fail('VAL_INVALID_INPUT', errors.join('; '));

  return ok(raw);
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

const WORKFLOW_ID = 'BB_04_Validate_Input';
const meta = () => ({ source: 'webhook', timestamp: new Date().toISOString(), workflow_id: WORKFLOW_ID });
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];
try {
  const raw = $input.first()?.json;
  return ok(raw);
} catch (e) {
  return [{ json: { success: false, error_code: 'INTERNAL_ERROR', error_message: `${WORKFLOW_ID}: ${e.message}`, data: null, _meta: meta() } }];
}

