const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_05_Circuit_Breaker_Helper';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'circuit_breaker', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

const THRESHOLD = 5;
const COOLDOWN_SECONDS = 60;

try {
  const items = $input.all();
  if (!items?.length) return fail('VAL_NO_INPUT', 'No input received');

  const raw = items[0].json.body || items[0].json;
  const workflowName = raw.workflow_name;
  const operation = raw.operation || 'check';

  if (!workflowName) return fail('VAL_MISSING_WORKFLOW', 'workflow_name is required');

  return ok({
    workflow_name: workflowName,
    operation: operation,
    threshold: THRESHOLD,
    cooldown_seconds: COOLDOWN_SECONDS,
    timestamp: DateTime.now().setZone(TIMEZONE).toISO()
  });
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_05_Circuit_Breaker_Helper';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'circuit_breaker', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

const THRESHOLD = 5;
const COOLDOWN = 60;

try {
  const items = $input.all();
  const workflowName = $('Guard').item.json.data.workflow_name;
  const now = DateTime.now().setZone(TIMEZONE);

  // No record = CLOSED (allow)
  if (!items?.length || (items.length === 1 && Object.keys(items[0].json).length === 0)) {
    return ok({ workflow_name: workflowName, state: 'CLOSED', allow: true, reason: 'no_record' });
  }

  const record = items[0].json;

  // If OPEN, check if cooldown passed
  if (record.state === 'OPEN') {
    const nextAttempt = record.next_attempt_at ? DateTime.fromISO(record.next_attempt_at) : null;

    if (nextAttempt && now >= nextAttempt) {
      return ok({ workflow_name: workflowName, state: 'HALF_OPEN', allow: true, reason: 'cooldown_passed', record_id: record.id });
    }

    return fail('SYS_CIRCUIT_OPEN', `Circuit is OPEN for ${workflowName}. Next attempt at ${nextAttempt?.toISO() || 'unknown'}`);
  }

  // If HALF_OPEN, allow one test request
  if (record.state === 'HALF_OPEN') {
    return ok({ workflow_name: workflowName, state: 'HALF_OPEN', allow: true, reason: 'test_request', record_id: record.id });
  }

  // CLOSED - check failure count
  if (record.failure_count >= THRESHOLD) {
    return fail('SYS_CIRCUIT_OPEN', `Circuit OPEN due to ${record.failure_count} failures`);
  }

  return ok({ workflow_name: workflowName, state: 'CLOSED', allow: true, failure_count: record.failure_count, record_id: record.id });
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_05_Circuit_Breaker_Helper';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'circuit_breaker', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const result = $input.first()?.json;
  const operation = $('Guard').item.json.data.operation;

  return ok({
    operation: operation,
    workflow_name: result?.workflow_name || $('Guard').item.json.data.workflow_name,
    state: result?.state || 'updated',
    failure_count: result?.failure_count ?? 0,
    timestamp: DateTime.now().setZone(TIMEZONE).toISO()
  });
} catch (e) {
  return [{ json: { success: true, error_code: null, error_message: null, data: { operation: 'completed', warning: e.message }, _meta: meta() } }];
}
