const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_05_Notification_Engine';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'notification', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const notifications = $input.first().json.data.notifications;

  // Format for Telegram
  const formatted = notifications.map(n => ({
    notification_id: n.id,
    chat_id: n.recipient || '5391760292',
    text: n.message,
    parse_mode: 'Markdown',
    original: n
  }));

  return ok({ formatted, count: formatted.length });
} catch (e) {
  return [{ json: { success: false, error_code: 'INTERNAL_ERROR', error_message: `${WORKFLOW_ID}: ${e.message}`, data: null, _meta: meta() } }];
}

const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_05_Notification_Engine';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'notification', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });

try {
  // Get results from Telegram sends
  const items = $input.all();
  const results = items.map(item => {
    const original = item.json.original || {};
    const telegramResult = item.json;

    return {
      notification_id: original.id || telegramResult.notification_id,
      success: !!telegramResult.message_id,
      message_id: telegramResult.message_id || null,
      chat_id: telegramResult.chat?.id || original.recipient
    };
  });

  return results.map(r => ({ json: r }));
} catch (e) {
  return [{ json: { success: false, notification_id: null, error: e.message } }];
}

