const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_07_Notification_Retry_Worker';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'retry_worker', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const items = $input.all();

  if (!items?.length || (items.length === 1 && Object.keys(items[0].json).length === 0)) {
    return ok({ notifications: [], count: 0, message: 'No failed notifications to retry' });
  }

  const notifications = items.map(item => {
    const n = item.json;
    return {
      id: n.id,
      booking_id: n.booking_id,
      user_id: n.user_id,
      message: n.message,
      channel: n.channel || 'telegram',
      recipient: n.recipient,
      priority: n.priority || 5,
      payload: n.payload || {},
      retry_count: n.retry_count || 0,
      max_retries: n.max_retries || 3,
      error_message: n.error_message
    };
  });

  return ok({ notifications, count: notifications.length });
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

