const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_07_Notification_Retry_Worker';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';

try {
  const notifications = $input.first().json.data.notifications;

  return notifications.map(n => ({
    json: {
      notification_id: n.id,
      chat_id: n.recipient || '5391760292',
      text: n.message,
      parse_mode: 'Markdown',
      retry_count: n.retry_count,
      max_retries: n.max_retries
    }
  }));
} catch (e) {
  return [{ json: { error: e.message } }];
}

const { DateTime } = require('luxon');

try {
  const input = $input.first()?.json || {};
  const notificationId = input.notification_id || $('Split Items').item.json.notification_id;
  const retryCount = input.retry_count || $('Split Items').item.json.retry_count || 0;
  const maxRetries = input.max_retries || $('Split Items').item.json.max_retries || 3;

  const success = !!(input.message_id || input.ok);
  const newRetryCount = retryCount + 1;
  const canRetry = newRetryCount < maxRetries;

  return [{
    json: {
      notification_id: notificationId,
      success: success,
      retry_count: newRetryCount,
      max_retries: maxRetries,
      can_retry: canRetry,
      error: success ? null : (input.error_message || input.description || 'Unknown error')
    }
  }];
} catch (e) {
  return [{ json: { success: false, error: e.message } }];
}

