const WORKFLOW_ID = 'BB_08_JWT_Auth_Helper';
const meta = () => ({ source: 'webhook', timestamp: new Date().toISOString(), workflow_id: WORKFLOW_ID });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const items = $input.all();
  if (!items?.length) return fail('VAL_NO_INPUT', 'No input received');

  const raw = items[0].json.body || items[0].json;
  const headers = items[0].json.headers || raw.headers || {};

  const authHeader = headers.authorization || headers.Authorization || '';

  if (!authHeader) return fail('SEC_UNAUTHORIZED', 'Missing Authorization header');
  if (!authHeader.startsWith('Bearer ')) return fail('SEC_INVALID_TOKEN', 'Invalid token format');

  const token = authHeader.substring(7);

  try {
    const parts = token.split('.');
    if (parts.length === 3) {
      let base64 = parts[1].replace(/-/g, '+').replace(/_/g, '/');
      while (base64.length % 4) base64 += '=';
      const payload = JSON.parse(Buffer.from(base64, 'base64').toString());
      if (payload.exp && payload.exp < Math.floor(Date.now() / 1000)) {
        return fail('SEC_EXPIRED_TOKEN', 'Token has expired');
      }
    }
  } catch (e) {
    // If decoding fails, we still pass it along but log it's not a standard JWT
  }

  return ok({
    token: token,
    validated: true,
    token_length: token.length
  });
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

const WORKFLOW_ID = 'BB_08_JWT_Auth_Helper';
const meta = () => ({ source: 'webhook', timestamp: new Date().toISOString(), workflow_id: WORKFLOW_ID });
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];
try {
  const raw = $input.first()?.json;
  return ok(raw);
} catch (e) {
  return [{ json: { success: false, error_code: 'INTERNAL_ERROR', error_message: `${WORKFLOW_ID}: ${e.message}`, data: null, _meta: meta() } }];
}

