const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_95_Provider_Cache';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'cache', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const fail = (code, msg) => [{ json: { success: false, error_code: code, error_message: msg, data: null, _meta: meta() } }];
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

const CACHE_TTL_MINUTES = 30;

try {
  const items = $input.all();
  if (!items?.length) return fail('VAL_NO_INPUT', 'No input received');

  const raw = items[0].json.body || items[0].json;
  const providerSlug = raw.provider_slug;
  const operation = raw.operation || 'get';

  if (!providerSlug) return fail('VAL_MISSING_SLUG', 'provider_slug is required');

  return ok({
    provider_slug: providerSlug,
    operation,
    ttl_minutes: CACHE_TTL_MINUTES
  });
} catch (e) {
  return fail('INTERNAL_ERROR', `${WORKFLOW_ID}: ${e.message}`);
}

const { DateTime } = require('luxon');
const WORKFLOW_ID = 'BB_95_Provider_Cache';
const VERSION = 'v1.0';
const TIMEZONE = $vars.TIMEZONE || 'UTC';
const meta = () => ({ source: 'cache', timestamp: DateTime.now().setZone(TIMEZONE).toISO(), workflow_id: WORKFLOW_ID, version: VERSION });
const ok = (data) => [{ json: { success: true, error_code: null, error_message: null, data, _meta: meta() } }];

try {
  const cacheRecord = $input.first()?.json;
  const providerData = cacheRecord.data || cacheRecord;

  return ok({
    provider: providerData,
    from_cache: !!cacheRecord.cached_at,
    cached_at: cacheRecord.cached_at || DateTime.now().setZone(TIMEZONE).toISO()
  });
} catch (e) {
  return [{ json: { success: false, error_code: 'INTERNAL_ERROR', error_message: `${WORKFLOW_ID}: ${e.message}`, data: null, _meta: meta() } }];
}

